import java.util.ArrayList;import java.util.List;public class FindAllAnagram {	public List<Integer> findAnagrams(String s, String p) {        List<Integer> res = new ArrayList<Integer>();        if(s == null || p == null || s.length() == 0 || p.length() == 0){            return res;        }        int[] hash = new int[26];        for(char ch : p.toCharArray()){            hash[ch - 'a']++;        }                int left = 0, right = 0, count = p.length();        while(right < s.length()){            char l = s.charAt(left);            char r = s.charAt(right);            if(hash[r - 'a'] > 0) count--;            hash[r - 'a']--;            right++;            if(count == 0) res.add(left);            if(right - left == p.length()){                if(hash[l - 'a'] >= 0) count++;                hash[l - 'a']++;                left++;            }        }        return res;    }}