import java.util.ArrayList;import java.util.List;public class PascalTriangle {	public List<List<Integer>> generate(int numRows) {        List<List<Integer>> res = new ArrayList<List<Integer>>();        if(numRows < 1) return res;        List<Integer> pre = new ArrayList<Integer>();        for(int i = 1; i <= numRows; i++){            List<Integer> sol = new ArrayList<Integer>();            for(int idx = 0; idx < i; idx++){                if(idx == 0 || idx == i - 1) sol.add(1);                else{                    sol.add(pre.get(idx - 1) + pre.get(idx));                }            }            res.add(sol);            pre = sol;        }        return res;    }}