import java.util.*;public class ReletiveRanks {	public String[] findRelativeRanks(int[] nums) {        if(nums == null || nums.length == 0){            return new String[0];        }        String[] res = new String[nums.length];        HashMap<Integer, Integer> map = new HashMap<Integer, Integer>();        for(int i = 0; i < nums.length; i++){            map.put(nums[i], i);        }        Integer[] tmp = new Integer[nums.length];        for(int i = 0; i < nums.length; i++){            tmp[i] = nums[i];        }        Arrays.sort(tmp, new Comparator<Integer>(){            public int compare(Integer e1, Integer e2){                return e2 - e1;            }        });        for(int i = 0; i < tmp.length; i++){            int index = map.get(tmp[i]);            if(i == 0){                res[index] = "Gold Medal";            } else if(i == 1){                res[index] = "Silver Medal";            } else if(i == 2){                res[index] = "Bronze Medal";            } else {                res[index] = i + 1 + "";            }        }        return res;    }}