public class SearchA2DMatrix2 {	/**     * @param matrix: A list of lists of integers     * @param: A number you want to search in the matrix     * @return: An integer indicate the occurrence of target in the given matrix     */    public int searchMatrix(int[][] matrix, int target) {        if(matrix == null || matrix.length == 0 || matrix[0].length == 0) return 0;         int row = matrix.length, col = matrix[0].length;        int i = 0, j = col - 1, count = 0;        while(i < row && j >= 0){            if(matrix[i][j] < target){                i++;            }            else if(matrix[i][j] > target){                j--;            }            else{                count++;                i++;                j--;            }        }        return count;    }}