public class StrobogrammaticNumber {	public boolean isStrobogrammatic(String num) {        if(num == null || num.length() == 0) return true;        int left = 0, right = num.length() - 1;        while(left < right){            char a = num.charAt(left), b = num.charAt(right);            if(a == b){                if(a != '1' && a != '8' && a != '0'){                    return false;                }            } else{                if(a == '6' && b == '9' || (a == '9' && b == '6')){                    left++;                    right--;                    continue;                } else {                    return false;                }            }            left++;            right--;        }        if(left == right){            if(num.charAt(left) != '8' && num.charAt(left) != '1' && num.charAt(left) != '0'){                return false;            }        }        return true;    }}