public class ClimbingStairs {	/**     * @param n an integer     * @return an integer     */    public int climbStairs2(int n) {        if(n < 0) return 0;        int[] dp = new int[n+1];        dp[0] = 1;        for(int i = 1; i <= n; i++){            int start = Math.max(0, i - 3);            for(int j = start; j < i; j++){                dp[i] += dp[j];            }        }        return dp[n];    }}