public class AddTwoNumbers {	public class ListNode {		      int val;		      ListNode next;		      ListNode(int x) {		          val = x;		          next = null;      		      }		  }	/**     * @param l1: the first list     * @param l2: the second list     * @return: the sum list of l1 and l2      */    public ListNode addLists(ListNode l1, ListNode l2) {        if(l1 == null && l2 == null) return null;        if(l1 == null) return l2;        if(l2 == null) return l1;        ListNode res = new ListNode(-1);        ListNode p1 = l1, p2 = l2, p3 = res;        int extra = 0;        while(p1 != null && p2 != null){            int sum = p1.val + p2.val + extra;            extra = sum / 10;            ListNode cur = new ListNode(sum % 10);            p3.next = cur;            p3 = p3.next;            p1 = p1.next;            p2 = p2.next;        }        if(p1 != null){            p1.val += extra;            p3.next = p1;        }        else if(p2 != null){            p2.val += extra;            p3.next = p2;        } else {            if(extra != 0){                p3.next = new ListNode(extra);            }        }        return res.next;    }}