public class GameOfLife {	public void gameOfLife(int[][] board) {        int row = board.length, col = board[0].length;        for(int i = 0; i < row; i++){            for(int j = 0; j < col; j++){                int lives = countLives(board, i , j, row, col);                if(board[i][j] == 1 && (lives == 2 || lives == 3)) {                    board[i][j] = 3;                } else if (board[i][j] == 0 && lives == 3) {                    board[i][j] = 2;                }            }        }        for(int i = 0; i < row; i++){            for(int j = 0; j < col; j++){                board[i][j] = board[i][j] >> 1;            }        }    }        private int countLives(int[][] board, int i , int j, int row, int col){        int startX = Math.max(i-1, 0), endX = Math.min(i+1, row-1);        int startY = Math.max(j-1, 0), endY = Math.min(j+1, col-1);        int lives = 0;        for(int x = startX; x <= endX; x++){            for(int y = startY; y <= endY; y++){                if(x == i && y == j) continue;                if((board[x][y] & 1) == 1) lives++;            }        }        return lives;    }}