public class LicenseKeyFormatting {	public String licenseKeyFormatting(String S, int K) {        if(S == null || S.length() == 0 || K == 0){            return "";        }        StringBuilder sb = new StringBuilder();        int count = 0;        for(int i = S.length() - 1; i >= 0; i--){            char ch = S.charAt(i);            if(ch == '-'){                continue;            } else {                if(ch >= '0' && ch <= '9'){                    sb.insert(0, ch);                    count++;                } else if(ch >= 'a' && ch <= 'z'){                    sb.insert(0, Character.toUpperCase(ch));                    count++;                } else if(ch >= 'A' && ch <= 'Z'){                    sb.insert(0, ch);                    count++;                }            }            if(count == K){                sb.insert(0, '-');                count = 0;            }        }        if(sb.length() != 0 && sb.charAt(0) == '-'){            sb.deleteCharAt(0);        }        return sb.toString();    }}