import java.util.*;public class UniqueWordAbbreviation {	HashMap<String, Integer> map = new HashMap<String, Integer>();    HashSet<String> set = new HashSet<String>();    public UniqueWordAbbreviation(String[] dictionary) {        for(String str : dictionary){            if(set.contains(str)) continue;            set.add(str);            String each = getAbbr(str);            if(!map.containsKey(each)){                map.put(each, 1);            } else {                map.put(each, map.get(each) + 1);            }        }    }    public boolean isUnique(String word) {        String str = getAbbr(word);        if(!set.contains(word)){            if(map.containsKey(str)){                return false;            }        } else {            if(map.get(str) >= 2){                return false;            }        }        return true;    }        private String getAbbr(String word){        StringBuilder sb = new StringBuilder();        if(word.length() <= 2){            sb.append(word);        } else {            sb.append(word.charAt(0)).append(word.length() - 2).append(word.charAt(word.length() - 1));        }        return sb.toString();    }}