import java.util.*;public class ZigZagIterator {	Queue<Iterator> list;        public ZigZagIterator(List<Integer> v1, List<Integer> v2) {        list = new LinkedList<Iterator>();        if(v1.size() != 0){            list.offer(v1.iterator());        }        if(v2.size() != 0){            list.offer(v2.iterator());        }    }    public int next() {        if(!this.hasNext()) return -1;        Iterator it = this.list.poll();        int res = (int)it.next();        if(it.hasNext()){            list.offer(it);        }        return res;    }    public boolean hasNext() {        if(list.isEmpty()) return false;        return true;    }}