public class BinaryTreeMaxPathSum2 {	public class TreeNode {		      public int val;		      public TreeNode left, right;		      public TreeNode(int val) {		          this.val = val;		          this.left = this.right = null;		      }		  }	/**     * @param root the root of binary tree.     * @return an integer     */    public int maxPathSum2(TreeNode root) {        if(root == null) return 0;        int left = maxPathSum2(root.left);        int right = maxPathSum2(root.right);        int max = Math.max(left, right) + root.val;        max = Math.max(max, root.val);        return max;    }	}