import java.util.ArrayList;public class BinaryTreePostOrderTraversal {	public class TreeNode {		      public int val;		      public TreeNode left, right;		      public TreeNode(int val) {		          this.val = val;		          this.left = this.right = null;		      }		  }	/**     * @param root: The root of binary tree.     * @return: Postorder in ArrayList which contains node values.     */    public ArrayList<Integer> postorderTraversal(TreeNode root) {        ArrayList<Integer> res = new ArrayList<Integer>();        if(root == null) return res;        postorderTraversal(root, res);        return res;    }        private void postorderTraversal(TreeNode root, ArrayList<Integer> res){        if(root == null) return;        postorderTraversal(root.left, res);        postorderTraversal(root.right, res);        res.add(root.val);    }}