public class MinimumDepthOfBinaryTree {	public class TreeNode {		      public int val;		      public TreeNode left, right;		      public TreeNode(int val) {		          this.val = val;		          this.left = this.right = null;		      }		  }	/**     * @param root: The root of binary tree.     * @return: An integer.     */    public int minDepth(TreeNode root) {        if(root == null) return 0;        if(root.left == null && root.right == null) return 1;        int d1 = root.left == null ? Integer.MAX_VALUE : 1 + minDepth(root.left);        int d2 = root.right == null ? Integer.MAX_VALUE : 1 + minDepth(root.right);        return Math.min(d1, d2);    }}