import java.util.HashMap;public class LongestSubstringWithoutRepeat {	/**     * @param s: a string     * @return: an integer      */    public int lengthOfLongestSubstring(String s) {        if(s == null || s.length() == 0) return 0;        HashMap<Character, Integer> map = new HashMap<Character, Integer>();        int left = 0, right = 0, max = 0, len = 0;        for(; right < s.length(); right++){            if(!map.containsKey(s.charAt(right)) || map.get(s.charAt(right)) < left) {                map.put(s.charAt(right), right);                len++;            }            else {                max = Math.max(max, len);                left = map.get(s.charAt(right));                map.put(s.charAt(right), right);                len = right - left;            }        }        return Math.max(max, len);    }}